import React, { Component } from "react";
import { View, Image, StyleSheet, FlatList, Text } from "react-native";
import { styleValues } from "../HelperFiles/StyleSheet";
import PropTypes from 'prop-types';
import { useNavigation } from "@react-navigation/native";

type Props = {
    images: string[],
    loadResponse?: () => void,
}

type State = {
    showGalleryIcon: boolean,
    images: Image[],
    galleryHeight: number,
}

export default class ImageSlider extends Component<Props, State> {

    state = {
        showGalleryIcon: true,
        images: [],
        galleryHeight: this.getGalleryHeight()
    }

    // Format the gallery height to match the aspect ratio of the first gallery image (run callback on completion)
    getGalleryHeight() {
        if (this.props.images.length > 0) {
            const assetSource = Image.resolveAssetSource(this.props.images[0])
            const ratio = assetSource.height / assetSource.width
            const height = (styleValues.winWidth - styleValues.mediumPadding*2) * ratio
            if (ratio >= 0.66) {
                return (styleValues.winWidth - styleValues.mediumPadding*2) * 0.66
            } else if (ratio <= 0.25) {
                return (styleValues.winWidth - styleValues.mediumPadding*2) * 0.25
            }
            return height
        } else {
            return styleValues.winHeight / 4
        }
    }

    renderGallery() {
        if (this.props.images.length > 0) {
            return (
                <FlatList
                style={styles.gallery}
                data={this.state.images}
                keyExtractor={(item, index) => index.toString()}
                onScroll={(event) => {
                    this.setState({showGalleryIcon: event.nativeEvent.contentOffset.x == 0});
                }}
                ListHeaderComponent={<View style={{width: styleValues.mediumPadding}}/>}
                ListFooterComponent={<View style={{width: styleValues.mediumPadding}}/>}
                ItemSeparatorComponent={() => <View style={{width: styleValues.mediumPadding}}/>}
                renderItem={({ item }) => item}
                horizontal={true}
                extraData={this.state}
                />
            )
        } else {
            return (
                <View style={[styles.gallery, {
                    backgroundColor: styleValues.lightColor,
                    borderWidth: styleValues.minorBorderWidth,
                    borderRadius: styleValues.bordRadius,
                    borderColor: styleValues.darkColor,
                    alignItems: "center",
                    justifyContent: "center"
                }]}>
                    <Text style={{
                        color: styleValues.darkColor,
                        fontSize: styleValues.smallerTextSize
                    }}>
                        There are no images to show.
                    </Text>
                </View>
            )
        }
    }

    // Render the gallery icon that shows when there are multiple images in the gallery
    renderGalleryIcon() {
        if (this.state.showGalleryIcon) {
            return (
                <Image 
                    style={[styles.headerIcon, {}]}
                    resizeMethod={"scale"}
                    resizeMode={"contain"}
                    source={require("../../assets/stackedSquares.png")}
                />
            )
        }
    }

    render() {
        return (
            <View style={{width: "100%", height: this.state.galleryHeight, marginBottom: styleValues.mediumPadding}}>
                {this.renderGallery()}
                {this.renderGalleryIcon()}
                {this.props.children}
            </View>
        );
    }
}

const styles = StyleSheet.create({
    gallery: {
        width: "100%",
        height: "100%",
    },
    headerIcon: {
        position: "absolute",
        right: 0,
        bottom: 0,
        margin: styleValues.mediumPadding,
        width: styleValues.iconMediumSize,
        height: styleValues.iconMediumSize,
        opacity: 0.75
    },
    photo: {

    }
});
